mapper.insert:
insert into
	board_table(
		board_no
		, member_id
		, board_title
		, board_context
		
		
			, originalfile
			, savedfile
		
	
	
	)values(
	
		board_seq.nextVal
		, #{member_id}
		, #{board_title}
		, #{board_context}
	
		
			, #{originalfile}
			, #{savedfile}
		
		
	)

mapper.update:
update	
	board_table
set
		
		board_title = #{board_title}
		, board_context = #{board_context}

where
	board_no = #{board_no}
